import { toPx } from "@microsoft/fast-jss-utilities";
import { applyInputStyle, applyLabelStyle, applyWrapperStyle, colors } from "../utilities/form-input.style";
import { ComponentStyles, ICSSRules } from "@microsoft/fast-jss-manager";
import { IFormItemThemeClassNameContract } from "../class-name-contracts/";

function applyThemeInputBackplateStyle(): ICSSRules<{}> {
    return {
        appearance: "none",
        border: `${toPx(2)} solid transparent`,
        height: toPx(36),
        width: toPx(36),
        "&:focus": {
            outline: "none",
        },
        "&:checked": {
            borderColor: colors.blue
        }
    };
}

const styles: ComponentStyles<IFormItemThemeClassNameContract, {}> = {
    formItemTheme: {
        ...applyWrapperStyle()
    },
    formItemTheme_label: {
        ...applyLabelStyle()
    },
    formItemTheme_input__light: {
        ...applyThemeInputBackplateStyle(),
        /* tslint:disable-next-line */
        background: "url(data:image/svg+xml;base64,) center no-repeat",
        backgroundColor: "transparent"
    },
    formItemTheme_input__dark: {
        ...applyThemeInputBackplateStyle(),
        /* tslint:disable-next-line */
        background: "url(data:image/svg+xml;base64,) center no-repeat",
        backgroundColor: colors.black
    }
};

export default styles;
